mapper.select:
SELECT '0' AS SEQ, '---' AS USERID, '' AS FRNDID, '---' AS FRND_NICNAME, '---' AS STATUS_MSG, '' AS PROFILE_IMG, CURRENT_TIMESTAMP AS REG_DATE, 0 AS FRND_COUNT FROM DUAL
UNION ALL
SELECT '0' AS SEQ, USERID, '' AS FRNDID, USER_NICNAME, STATUS_MSG, NVL(MY_PROFILE_IMG_URL,'/img/ProfileImg/default_img.png') AS PROFILE_IMG, REG_DATE, 0 AS FRND_COUNT FROM CHATUSER WHERE USERID= #{userid}
UNION ALL
SELECT '2' AS SEQ, '---' AS USERID, '' AS FRNDID, '---' AS FRND_NICNAME, '---' AS STATUS_MSG, '' AS PROFILE_IMG, CURRENT_TIMESTAMP AS REG_DATE, COUNT(FRNDID) AS FRND_COUNT FROM FRIENDLIST WHERE USERID = #{userid}
AND (SELECT USER_NICNAME FROM CHATUSER WHERE USERID = FRNDID) LIKE '%'||#{search}||'%'
UNION ALL
SELECT '2' AS SEQ, A.USERID, A.FRNDID, B.USER_NICNAME AS FRND_NICNAME, B.STATUS_MSG AS STATUS_MSG, NVL(MY_PROFILE_IMG_URL,'/img/ProfileImg/default_img.png') AS PROFILE_IMG, A.REG_DATE, 0 AS FRND_COUNT FROM FRIENDLIST A, CHATUSER B WHERE A.FRNDID = B.USERID AND A.USERID = #{userid}
AND (SELECT USER_NICNAME FROM CHATUSER WHERE USERID = FRNDID) LIKE '%'||#{search}||'%'


SELECT COUNT(*) AS COUNT
  FROM CHKREADMSG 
 WHERE ROOMID=#{roomid}
   AND USERID=#{userid}

mapper.update:

	UPDATE CHKREADMSG 
	   SET LASTREADDATE = CURRENT_TIMESTAMP 
	 WHERE USERID = #{userid} 
	   AND ROOMID = #{roomid}
